import React, { useState } from 'react';
import BlogList from './BlogList';
import '../Css/create.css';
import Resizer from 'react-image-file-resizer';


const BlogPage = () => {
  const [posts, setPost] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '', imageUrl: '' });
  const [selectedFile, setSelectedFile] = useState();
  const storedPosts = localStorage.getItem('blogPosts');
  useEffect(() => {
    if (storedPosts !=null) {
      setPost (JSON.parse(storedPosts));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('blogPosts', JSON.stringify(posts));
  }, [posts]);

  const handleAddPost = () => {
    if (newPost.title && newPost.content) {
      setPost([...posts, newPost]);
      setNewPost({ title: '', content: '', imageUrl: '' });
      alert('Successfully created new post');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      Resizer.imageFileResizer(
        file,
        600, // Max width
        400, // Max height
        'JPEG',
        100,
        0, // Rotation
        (resizedImage) => {
          setNewPost({ ...newPost, imageUrl: resizedImage });
          setSelectedFile(file);
        },
        'base64'
      );
    }
  };
  
  return (
      <div>
        <div className="form-container">
          <div className="container-actual">
            <h1><b>What will be your post all about?</b></h1>
            <p>Share your thoughts, ideas, and experiences on our user-friendly post<br></br> creation page. Your voice matters, and we're excited to see what you<br></br> create. Start composing your post now and join the conversation!</p>
            <label><b>Post Title:</b></label>
            <input
              type="text"
              placeholder="The title of my post is..."
              value={newPost.title}
              onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
            />
            <br></br>
            <label><b>Content:</b></label>
            <textarea
              rows="10"
              placeholder="The content covering my post's topic is..."
              value={newPost.content}
              onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
            />
            <br></br>
            <label><b>Image:</b></label>
            <div className="button-wrapper">
              <input id="file-button" type="file" accept="image/*" onChange={handleImageChange} hidden/>
              <label htmlFor="file-button">Choose Image</label>
            </div><br></br>
            {selectedFile && (
              <span><b>Chosen Image:</b> {selectedFile.name}</span>
            )}
            <br></br><br></br>
            
            <button onClick={handleAddPost}>Add Post</button>
          </div>
          <img className="image-1" src="img/miffy1.png"/>
        </div>
        <BlogList post={posts} />
      </div>
  );
};

export default BlogPage;
