import React, { useState } from 'react';
import BlogList from './BlogList';
import '../Css/create.css';
import Resizer from 'react-image-file-resizer';

const BlogPage = () => {
  const [posts, setPost] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '', imageUrl: '' });

  const handleAddPost = () => {
    if (newPost.title && newPost.content) {
      setPost([...posts, newPost]);
      setNewPost({ title: '', content: '', imageUrl: '' });
      alert('Successfully created new post');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      Resizer.imageFileResizer(
        file,
        600, // Max width
        400, // Max height
        'JPEG',
        100,
        0, // Rotation
        (resizedImage) => {
          setNewPost({ ...newPost, imageUrl: resizedImage });
        },
        'base64'
      );
    }
  };

  return (
    <div>
      <div className="form-container">
        <h1>My Blog</h1>
        <label className= "bp">Post Title</label>
        <br></br>
        <input
          type="text"
          className= "bp"
          placeholder="Title"
          value={newPost.title}
          onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
        />
        <br></br>
        <label className= "bp">Content</label>
        <br></br>
        <textarea
          className= "bp"
          placeholder="Content"
          value={newPost.content}
          onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
        />
        <br></br>
        <label className= "bp">Image:</label>
        <br></br>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <div>
          <br></br>
        </div>
        
        <div>
          <br></br>
        </div>
        <BlogList post={posts} />
        <button onClick={handleAddPost}>Add Post</button>
      </div>
      
    </div>
  );
};

export default BlogPage;
