import React, {useState} from 'react';
import BlogList from './BlogList';

const BlogPage =() => {
    const [posts, setPost] = useState ([
        {title:'', content:'', imageUrl: ''}
    ]);

    const [newPost, setNewPost]=useState({title:'', content:'', imageUrl: ''});

    const handleAddPost = () => {
        if(newPost.title && newPost.content){
            setPost([...posts,newPost]);
            setNewPost({title:'',content:'', imageUrl: ''});
        }
    };
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
          const reader = new FileReader();
          reader.onloadend = () => {
            const img = new Image();
            img.src = reader.result;
      
            img.onload = () => {
              const maxWidth = 800; // Set your desired maximum width
              const maxHeight = 800; // Set your desired maximum height
      
              let newWidth = img.width;
              let newHeight = img.height;
      
              // Resize the image if it exceeds the maximum dimensions
              if (img.width > maxWidth || img.height > maxHeight) {
                const aspectRatio = img.width / img.height;
      
                if (img.width > maxWidth) {
                  newWidth = maxWidth;
                  newHeight = newWidth / aspectRatio;
                }
      
                if (newHeight > maxHeight) {
                  newHeight = maxHeight;
                  newWidth = newHeight * aspectRatio;
                }
              }
      
              const canvas = document.createElement('canvas');
              canvas.width = newWidth;
              canvas.height = newHeight;
      
              const ctx = canvas.getContext('2d');
              ctx.drawImage(img, 0, 0, newWidth, newHeight);
      
              const resizedImageUrl = canvas.toDataURL(file.type);
      
              setNewPost({ ...newPost, imageUrl: resizedImageUrl });
            };
          };
      
          reader.readAsDataURL(file);
        }
      };

    return(
        <div>
            <h1>My Blog</h1>
            
            <BlogList post={posts}/>
            <input type="text"
            placeholder="Title"
            value={newPost.title}
            onChange={(e)=> setNewPost({...newPost, title: e.target.value})}
            />
            <textarea
            placeholder="Content"
            value={newPost.content}
            onChange={(e)=> setNewPost({...newPost, content: e.target.value})}
            />
             <div>
            <label>Image:</label>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            </div>
            <button onClick={handleAddPost}>Add Post</button>
        </div>
    );
}

export default BlogPage;
