import React, {useState} from 'react';
import BlogList from './BlogList';
//downloaded resizer library for image
import Resizer from 'react-image-file-resizer';


const BlogPage =() => {
    const [posts, setPost] = useState ([
        {title:'', content:'', imageUrl: ''}
    ]);

    const [newPost, setNewPost]=useState({title:'', content:'', imageUrl: ''});

    const handleAddPost = () => {
        if(newPost.title && newPost.content){
            setPost([...posts,newPost]);
            setNewPost({title:'',content:'', imageUrl: ''});
            alert('Successfully created new post');
        }
    };
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            Resizer.imageFileResizer(
                file,
                600, // Max width
                400, // Max height
                'JPEG', // Output format
                100, // Quality (0 to 100)
                0, // Rotation (0 for no rotation)
                (resizedImage) => {
                  setNewPost({ ...newPost, imageUrl: resizedImage });
                },
                'base64' // Output type ('base64' or 'file')
              );
        }
      };

    return(
        <div>
        <div class="form-group">
            <h1>My Blog</h1>
            <label for="exampleInputEmail1">Post Title</label>
            <input type="text" 
            class="form-control"
            placeholder="Title"
            value={newPost.title}
            onChange={(e)=> setNewPost({...newPost, title: e.target.value})}
            />
        </div>
        <div class="form-group">
            <label for="exampleInputPassword1">Content</label>
            <textarea
            placeholder="Content"
            value={newPost.content}
            onChange={(e)=> setNewPost({...newPost, content: e.target.value})}
            />
             <div>
            <label>Image:</label>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            </div>
            <button onClick={handleAddPost}>Add Post</button>
            <BlogList post={posts}/>
        </div>
        </div>
    );
}

export default BlogPage;
